import React, { useState, useEffect } from 'react';
import Help from './Help';
import BasicInfo from './BasicInfo';
import BaseSpirit from './BaseSpirit';
import Ingredients from './Ingredients';
import Instructions from './Instructions';
import {
  Collapse,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  Typography,
} from '@material-ui/core';

// You may need to refer to this: https://stackoverflow.com/questions/61215349/material-ui-stepper-not-keeping-state-when-move-next-or-back
function renderStep(step) {
  switch (step) {
    case 0:
      return <BasicInfo />
    case 1:
      return <BaseSpirit />
    case 2:
      return <Ingredients />
    case 3: 
      return <Instructions />
    default:
      return false
  }
}

export default function CocktailStepper({ steps, activeStep }) {
  const [checked, setChecked] = useState(false);
  const [showHelp, setShowHelp] = useState(false);

  useEffect(() => {
    setChecked(true);
  }, []);

  const handleHelp = () => {
    setShowHelp(!showHelp)
  }

  return (
    <Collapse appear in={checked} {
      ... checked ? { timeout: 1000 } : {}
    }>
      <Stepper 
        id={`step-${activeStep}`} 
        activeStep={activeStep} 
        orientation="vertical"
      >
        {
          steps.map((label, index) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
              <StepContent>
                <Typography>{renderStep(index)}</Typography>
                {
                  // Stop showing the secondary ExpandMoreIcon if you've reached the Instructions step
                  // Also, this ExpandMoreIcon should push data to the recipe object array
                  /*index < steps.length - 1
                    ? <IconButton color="primary">
                        <ExpandMoreIcon onClick={handleNext} fontSize="large" />
                      </IconButton>
                    : false
                    */
                }
              </StepContent>
            </Step>
          ))
        }
      </Stepper>
    </Collapse>
  )
}
