import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import {
  Link,
  Typography,
  Card,
  CardMedia,
  CardContent,
  CardActionArea,
  Collapse,
  IconButton,
} from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { Link as Scroll } from 'react-scroll';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
  main: {
    minHeight: "100vh",
    backgroundImage: `url(${process.env.PUBLIC_URL + '/assets/cocktail1.jpg'})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center'
  },
  action: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    [theme.breakpoints.down('md')]: {
      flexDirection: "column",
    },
  },
  card: {
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    [theme.breakpoints.down('md')]: {
      flexDirection: "column",
    },
  },
  root: {
    background: 'rgba(0,0,0,0.5)',
    margin: '20px',
  },
  title: {
    fontFamily: 'Nunito',
    fontWeight: 'bold',
    fontSize: '2rem',
    color: '#fff',
  },
}));


function NavCard({title, navigate}) {
  const classes = useStyles();

  return (
    <Link style={{ textDecoration: 'none' }} onClick={navigate}>
      <Card className={classes.root}>
        <CardActionArea>
          <CardMedia
            className={classes.media}
  //          image={image}
            title="Navigate"
          />
          <CardContent>
            <Typography 
              gutterBottom 
              variant="h5" 
              component="h1" 
              className={classes.title}
            >
              {title}
            </Typography>
            <Typography variant="body2" color="textSecondary" component="p" className={classes.paragraph}>
              {"Description"}
            </Typography>
          </CardContent>
        </CardActionArea>
      </Card>
    </Link>
  );
}

export default function Start() {
  const classes = useStyles();
  const history = useHistory();
  const [checked, setChecked] = useState(false);

  useEffect(() => {
    setChecked(true)
  }, []);

  const onNavigate = (type) => {
    history.push(`register/${type}`);
  }

  return (
    <main>
      <div className={classes.main} id="header">
        <Collapse appear in={checked} { 
          ... checked ? { timeout: 1000 } : {}
        }>
          <div className={classes.container}>
            <Typography className={classes.title} component="h1" variant="h2">
              Welcome to<br/>
              The <span className={classes.colorText}>JADE </span>Family
            </Typography>
            <Scroll to="call-to-action" smooth="true">
              <IconButton>
                <ExpandMoreIcon className={classes.goDown} />
              </IconButton>
            </Scroll>
          </div>
        </Collapse>
      </div>
      <div className={classes.action} id='signUpNav'>
        <Typography>
          Are you signing in as
        </Typography>
        <div className={classes.card}>
          <NavCard title={"Customer"} navigate={() => onNavigate("customer")} />
          <NavCard title={"Mixologist"} navigate={() => onNavigate("mixologist")} />
          <NavCard title={"Business"} navigate={() => onNavigate("business")} />
        </div>
      </div>
    </main>
  );
}
