import React, { useState, useEffect } from 'react';
import MainBar from './MainBar';
import {
  Box,
  Typography,
  Collapse,
  IconButton,
  Container,
  TextField,
  Stepper,
  Step,
  StepLabel,
  StepContent,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  info: {
    minHeight: '100vh',    
    textAlign: 'center',
  },
  appBar: {
    position: 'sticky',
    top: 0,    
    zIndex: 1,
  },
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    borderRadius: 17,
    fontFamily: 'Nunito',
    marginTop: 50,
  },
  goDown: {
    fontSize: '4rem',    
  },
  textInput: {
    borderRadius: 37,
    width: 450,
    marginRight: 7,
  },
  longInput: {
    borderRadius: 37,
  },
  createForm: {
    minHeight: '100vh',
  },
}));

export default function Create() {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);
  const [showInfo, setShowInfo] = useState(false);
  useEffect(() => {
    setChecked(true);
  });

  return (
    <main className={classes.info}>
      <MainBar hasInfoIcon />
      <Collapse appear in={checked} {
        ... checked ? { timeout: 1000 } : {}
      }>
        <Container maxWidth="md" className={classes.formContainer}>
          <Stepper activeStep={activeStep} orientation="vertical">
            {steps.map((label, index) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
                <StepContent>
                  <Typography>{getStepContent(index)}</Typography>
                  <div className={classes.actionsContainer}>
                    <div>
                      <Button
                        disabled={activeStep === 0}
                        onClick={handleBack}
                        className={classes.button}
                      >
                        Back
                      </Button>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={handleNext}
                        className={classes.button}
                      >
                        {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                      </Button>
                    </div>
                  </div>
                </StepContent>
              </Step>
            ))}
          </Stepper>
          <TextField 
            id='name'
            label='Name'
            variant='outlined'
            margin='normal'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='creator'
            label='Creator'
            variant='outlined'
            margin='normal'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='location'
            label='Location'
            variant='outlined'
            margin='normal'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='date'
            variant='outlined'
            margin='normal'
            type='date'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='description'
            label='Description'
            variant='outlined'
            margin='normal'
            fullWidth
            multiline
            rows='6'
            rowsMax='10'
            InputProps={{
              className: classes.longInput
            }}
          />
        </Container>
        <Scroll to="createForm" smooth="true">
          <IconButton>
            <ExpandMoreIcon className={classes.goDown} />
          </IconButton>
        </Scroll>
      </Collapse>
      <div id="createForm" className={classes.createForm}>
        {/* The build form here */}
      </div>
    </main>
  );
}
